mapper.update:
update user
set user_pw = #{user_pw}
where user_id = #{user_id} and user_email = #{user_email}


update user
set   user_birth = #{user_birth},
    user_phone = #{user_phone}
    where user_id = #{user_id}

mapper.select:

select user_id, user_name, user_birth, user_phone, user_email, user_gender, user_date
from user
where user_id = #{user_id}
